package org.hippoecm.frontend.plugins.cms.dev.updater

        import org.onehippo.repository.update.BaseNodeUpdateVisitor
        import javax.jcr.Node

        class UpdaterTemplate extends BaseNodeUpdateVisitor {

          boolean doUpdate(Node node) {
            if(node.hasNode("hippo:configuration/hippo:domains/everywhere-siteusers")){
              Node embargoDomainNode = node.getNode("hippo:configuration/hippo:domains/everywhere-siteusers")
              embargoDomainNode.remove();
            }
            if(node.hasNode("hippo:configuration/hippo:initialize/domains-everywhere-siteusers")) {
              node.getNode("hippo:configuration/hippo:initialize/domains-everywhere-siteusers").remove();
            }
            if(node.hasNode("hippo:configuration/hippo:initialize/domains-everywhere-remove-siteusers")) {
              node.getNode("hippo:configuration/hippo:initialize/domains-everywhere-remove-siteusers").remove();
            }
            return false
          }

          boolean undoUpdate(Node node) {
            throw new UnsupportedOperationException('Updater does not implement undoUpdate method')
          }

        }
